mapper.select:
select b.wx_name as 'wxName', b.avatar_url as 'headPic', a.title as 'offerTitle', a.imgs as 'offerPic', a.fans_num as 'fanNums', a.price as 'price', a.id as 'id', c.offersTag as 'tag', a.remarks as 'remarks', h.dict_value as 'platName', h.dict_pic as 'platPic' from ho_offers a left join ho_user_basic b on a.user_id = b.id left join ho_dict h on h.id=a.user_plate left join ( select a.offer_id as 'offerId', GROUP_CONCAT(b.dict_value) as 'offersTag', GROUP_CONCAT(b.id) as 'offersTagId' from ho_offer_tag a left join ho_dict b on b.id =a.dict_id group by a.offer_id ) c on c.offerId=a.id where a.enable_flag='1' and a.status='AP'
and a.user_plate in #{platId}
and a.gender = #{sex}
order by ${orderBy}
